mapper.select:
select order_id, order_title, order_state, creator_name, order_address, create_time, order_number from order_card_detail
and order_state = #{orderState}
and worker_id = #{workerId}
and creator_id = #{creatorId}
and order_title like concat('%', #{orderName}, '%')

select
    count(*)
from
    order_card_detail


        and order_state = #{orderState}


        and worker_id = #{workerId}


        and creator_id = #{creatorId}

mapper.update:
update patrol_order
set order_state = #{orderState}
where order_id = #{orderId}
